sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetArrayValue = x_snc_devtools.GetArrayValue;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetValue = x_snc_devtools.GetValue;
	var GetRecord = x_snc_devtools.GetRecord;


	if (current.state == GetBacklogItemState('draft') )
	{
		if (current.opened_by != current.assigned_to)
		{
			if (GetStringValue(current.owner) != '')
			{
				current.assigned_to = current.owner;
			}
		}
	}	

	if (current.state == GetBacklogItemState('ready') || current.state == GetBacklogItemState('dev') || current.state == GetBacklogItemState('failed'))
	{
		if (GetStringValue(current.developer) != '')
		{
			current.assigned_to = current.developer;
		}
	}

	if (current.state == GetBacklogItemState('done') || current.state == GetBacklogItemState('passed') )
	{
		var strDevOps = '';
		var aComponents = GetArrayValue(current.components);
		if (aComponents.length == 1)
		{
			var grComponent = GetRecord('x_snc_agile_component',GetValue(aComponents,0));
			if (IsValidRecord(grComponent) == true)
			{
				if (GetStringValue(grComponent.devops) != '')
				{
					strDevOps = GetStringValue(grComponent.devops);
				}
			}
		}
		if (strDevOps == '')
		{
			strDevOps = GetStringValue(current.backlog.devops);
		}
		if (strDevOps != '')
		{
			current.assigned_to = strDevOps;
		}
	}	

	if (current.state == GetBacklogItemState('test'))
	{
		if (GetStringValue(current.tester) != '')
		{
			current.assigned_to = current.tester;
		}
		else if (GetStringValue(current.backlog.tester) != '')
		{
			current.assigned_to = current.backlog.tester;
		}		
	}

	if (current.state == GetBacklogItemState('prod'))
	{
		if (GetStringValue(current.owner) != '')
		{
			current.assigned_to = current.owner;
		}
		else if (GetStringValue(current.backlog.owner) != '')
		{
			current.assigned_to = current.backlog.owner;
		}
	}	


})(current, previous);
