sys_script_include.script:
function SetBacklogItemDisplayValue(grBacklogItem)
{
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	if (IsValidRecord(grBacklogItem) == false)
	{
		return;
	}

	grBacklogItem.display = grBacklogItem.number + ' - ' + grBacklogItem.getDisplayValue('state') + ' - ' + grBacklogItem.short_description;
	return;
}
